`timescale 1ns / 1ps

module rom (
    input [3:0] chr,
    output reg [4095:0] graph, // 64 * 64
    output reg [11:0] color
    // 0: black, 1: white
);

always @(*)
begin
    case (chr)
        0: graph = 4096';
        1: graph = 4096';
        2: graph = 4096';
        3: graph = 4096';
        4: graph = 4096';
        5: graph = 4096';
        6: graph = 4096';
        7: graph = 4096';
        8: graph = 4096';
        9: graph = 4096';
        10: graph = 4096';
        11: graph = 4096';
        default : graph = 0;
    endcase
    
    case (chr)
        0: color = 12'hcba;
        1: color = 12'heed;
        2: color = 12'heec;
        3: color = 12'hfb7;
        4: color = 12'hf96;
        5: color = 12'hf75;
        6: color = 12'hf53;
        7: color = 12'hec7;
        8: color = 12'hec6;
        9: color = 12'hec5;
        10: color = 12'hec3;
        11: color = 12'hec2;
    endcase
end

endmodule
